mapper.insert:
insert into
tb_menu (id, menu_name, create_user, create_time, update_user, update_time

    , content


    , router


    , role_key


    , status

)
values (#{id}, #{menuName}, #{createUser}, #{createTime}, #{updateUser}, #{updateTime}

    , #{content}


    , #{router}


    , #{roleKey}


    , #{status}

)

mapper.select:
select id, menu_name, content, router, role_key, status from tb_menu where
menu_name = #{menuName} and
is_deleted = 0 order by update_time desc limit #{pageNum}, #{pageSize}
