mapper.insert:
insert into
tb_role (id, role_name, create_user, create_time, update_user, update_time

    , content


    , status

)
values (#{id}, #{roleName}, #{createUser}, #{createTime}, #{updateUser}, #{updateTime}

    , #{content}


    , #{status}

)

mapper.select:
select id, role_name, content, status from tb_role where
role_name = #{roleName} and
is_deleted = 0 order by update_time desc limit #{pageNum}, #{pageSize}
