mapper.insert:
insert into
tb_user (id, phone, nick_name, create_user, create_time, update_user, update_time

    , email


    , password


    , icon


    , status

)
values (#{id}, #{phone}, #{nickName}, #{createUser}, #{createTime}, #{updateUser}, #{updateTime}

    , #{email}


    , #{password}


    , #{icon}


    , #{status}

)

mapper.update:
update
    tb_user_role
set
    role_id = #{roleId}
where
    user_id = #{userId}

mapper.select:
select
id, phone, email, nick_name, icon, status
from
tb_user
where
is_deleted = 0

    and phone = #{user.phone}


    and email = #{user.email}


    and nick_name = #{user.nickName}

order by
update_time desc
limit
${pageNum}, #{pageSize}
